(14, 'default13699635641', '', '', 'cai', '', '', '[phan-bon-cay-an-trai]', 'admin', 'NANOBENZO Chuyên cây thanh long', '&lt;p&gt;\r\n	Th&amp;agrave;nh phần: N : 0,5%, K2O : 2%, Kalinitro Chlorid: 0,5% Alpha-Naphthalene Acetic Acid, Bta-Naphtoxy Acetic Acid, Gibberellic Acid (GA-3) Phụ gia đặc biệt.&lt;/p&gt;', '&lt;p&gt;\r\n	&lt;strong&gt;Th&amp;agrave;nh phần&lt;/strong&gt;:&lt;br /&gt;\r\n	N : 0,5%, K2O : 2%, Kalinitro Chlorid: 0,5%&lt;br /&gt;\r\n	Alpha-Naphthalene Acetic Acid,&lt;br /&gt;\r\n	Bta-Naphtoxy Acetic Acid,&lt;br /&gt;\r\n	Gibberellic Acid (GA-3)&lt;br /&gt;\r\n	Phụ gia đặc biệt.&lt;br /&gt;\r\n	&lt;strong&gt;C&amp;ocirc;ng dụng&lt;/strong&gt;:&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; Gi&amp;uacute;p Thanh Long đ&amp;acirc;m c&amp;agrave;nh mạnh v&amp;agrave; đồng loạt.&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;K&amp;iacute;ch c&amp;acirc;y trổ sớm, nhiều hoa v&amp;agrave; trổ đồng loạt.&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;Tăng khả năng đậu tr&amp;aacute;i.&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;Hạn chế hiện tượng n&amp;aacute;m th&amp;acirc;n, kh&amp;ocirc; c&amp;agrave;nh, mềm tr&amp;aacute;i.&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; &amp;nbsp;Gi&amp;uacute;p tr&amp;aacute;i lớn nhanh, tr&amp;aacute;i to, tai xanh &amp;ndash; cứng &amp;ndash; dầy.&lt;/p&gt;', 'nanobenzo-chuyen-cay-thanh-long', '', '', '', 300000, 200000, 12, 'upload/8.jpg', 0, '', '[][]', 0, 0, 'active', '2013-05-31 09:26:04', '', '2013-05-31 09:28:25', ''),
(15, 'default13699637631', '', '', 'chai', '', '', '[phan-bon-cay-an-trai]', 'admin', 'Phân bón lá Foli 5, Siêu ra hoa - Đậu trái - Dưỡng', '', '&lt;p&gt;\r\n	&lt;strong&gt;Th&amp;agrave;nh phần&lt;/strong&gt;:&lt;br /&gt;\r\n	Alginate + chất chiết suất rong biển: 7000 ppm.&lt;br /&gt;\r\n	* N: 5%, P2O5: 10%, K2O: 10%&lt;br /&gt;\r\n	&lt;strong&gt;*Trung vi lượng&lt;/strong&gt;:&lt;br /&gt;\r\n	Mg: 300 ppm, S: 700 ppm, Cu: 100 ppm, Zn: 300 ppm, Fe: 100 ppm,&lt;br /&gt;\r\n	Mn: 100 ppm, B: 300 ppm, Mo: 50 ppm.&lt;br /&gt;\r\n	&lt;strong&gt;C&amp;ocirc;ng dụng&lt;/strong&gt;:&lt;br /&gt;\r\n	Gi&amp;uacute;p hoa trổ sớm &amp;ndash; đồng loạt &amp;ndash; hạn chế hoa bị nghẹn &amp;ndash; hạn chế rụng hoa sinh l&amp;yacute;.&lt;br /&gt;\r\n	Gi&amp;uacute;p ph&amp;aacute;t triển chồi, tăng khả năng đậu tr&amp;aacute;i.&lt;br /&gt;\r\n	Gi&amp;uacute;p tr&amp;aacute;i to hết cở, b&amp;oacute;ng tr&amp;aacute;i, tăng độ ngọt v&amp;agrave; nặng k&amp;yacute;.&lt;br /&gt;\r\n	Hạn chế n&amp;aacute;m tr&amp;aacute;i &amp;ndash; h&amp;eacute;o tr&amp;aacute;i &amp;ndash; nứt tr&amp;aacute;i &amp;ndash; thối đ&amp;iacute;t tr&amp;aacute;i.&lt;br /&gt;\r\n	Hạn chế bệnh thối cổ rễ, chết nhanh, v&amp;agrave;ng l&amp;aacute;, đốm l&amp;aacute; tr&amp;ecirc;n c&amp;aacute;c loại rau m&amp;agrave;u v&amp;agrave; c&amp;aacute;c loại c&amp;acirc;y ăn tr&amp;aacute;i kh&amp;aacute;c.&lt;/p&gt;', 'phan-bon-la-foli-5-sieu-ra-hoa--dau-trai--duong', '', '', '', 0, 0, 13, 'upload/sieu ha phen (chai).jpg', 0, '', '[][]', 0, 0, 'active', '2013-05-31 09:29:23', '', '2013-05-31 09:30:45', ''),
(16, 'default13699638811', '', '', 'chai', '', '', '[phan-bon-lua]', 'admin', 'Axít ngâm giống', '&lt;p&gt;\r\n	Th&amp;agrave;nh phần: NO3: 25%, CaO: 22%, Mn: 20000 ppm; Cu, Zn, Fe: 500 ppm.&lt;/p&gt;', '&lt;p&gt;\r\n	&lt;strong&gt;Th&amp;agrave;nh phần&lt;/strong&gt;:&lt;br /&gt;\r\n	NO3: 25%, CaO: 22%, Mn: 20000 ppm; Cu, Zn, Fe: 500 ppm.&lt;br /&gt;\r\n	&lt;strong&gt;C&amp;ocirc;ng dụng&lt;/strong&gt;:&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; Ph&amp;aacute; vở mi&amp;ecirc;n trạng hạt l&amp;uacute;a giống.&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; K&amp;iacute;ch th&amp;iacute;ch hạt l&amp;uacute;a giống nhanh nẩy mầm, ph&amp;aacute;t triển rễ, cung cấp dinh dưỡng cho mầm.&lt;br /&gt;\r\n	&amp;bull;&amp;nbsp;&amp;nbsp;&amp;nbsp; Gi&amp;uacute;p mầm l&amp;uacute;a to, khỏe, kh&amp;aacute;ng s&amp;acirc;u bệnh.&lt;/p&gt;', 'axit-ngam-giong', '', '', '', 0, 0, 14, 'upload/canxi-bo copy.jpg', 0, '', '[phanbonla][sanphamhot][sanphamkhuyenmai][sanphammoi][][]', 0, 0, 'active', '2013-05-31 09:31:21', '', '2013-05-31 09:34:27', ''),
(17, 'default13699639751', '', '', '', '', '', '[phan-bon-lua]', 'admin', 'DEMAX 601', '&lt;p&gt;\r\n	Th&amp;agrave;nh phần: N: 5%, P2O5: 3%, K2O: 2%&lt;/p&gt;', '&lt;p&gt;\r\n	&lt;strong&gt;Th&amp;agrave;nh phần&lt;/strong&gt;:&lt;br /&gt;\r\n	N: 5%,&amp;nbsp;&amp;nbsp;&amp;nbsp; P2O5: 3%,&amp;nbsp;&amp;nbsp;&amp;nbsp; K2O: 2%&lt;br /&gt;\r\n	&lt;strong&gt;Vi lượng&lt;/strong&gt;:&lt;br /&gt;\r\n	Mg: 80&amp;nbsp; ppm, S: 40 ppm, Cu: 90 ppm, Fe: 80 ppm, Zn: 70 ppm, Mn: 60 ppm,&lt;br /&gt;\r\n	B: 50 ppm, Mo: 30 ppm.&lt;br /&gt;\r\n	&lt;strong&gt;C&amp;ocirc;ng dụng&lt;/strong&gt;:&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp; K&amp;iacute;ch th&amp;iacute;ch ph&amp;aacute;t triển bộ rễ nhanh v&amp;agrave; nhiều.&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp; Giảm tỷ lệ hạt l&amp;eacute;p, chống rụng hạt.&lt;br /&gt;\r\n	&amp;nbsp;&amp;nbsp;&amp;nbsp; Tăng khả năng đề kh&amp;aacute;ng đối với s&amp;acirc;u bệnh, hạ ph&amp;egrave;n, giảm mặn.&lt;br /&gt;\r\n	Gi&amp;uacute;p hạt chắc, tr&amp;aacute;i to &amp;ndash; s&amp;aacute;ng &amp;ndash; đẹp.&lt;/p&gt;', 'demax-601', '', '', '', 0, 0, 15, 'upload/K-HUMATE DAU BO.jpg', 0, '', '[sanphammoi][][]', 0, 0, 'active', '2013-05-31 09:32:55', '', '2013-05-31 09:34:38', ''),
(18, 'default13699641311', '', '', '', '', '', '[phan-hc-cay-an-trai]', 'admin', 'Phân hữu cơ sinh học (chuyên cây ăn trái)', '&lt;p&gt;\r\n	TH&amp;Agrave;NH PHẦN: N: 2.5% ; P2O5: 2.5% ; K2O : 2.5%.Trung vi lượng :Cao, 0.5%, Mg: 0.2%, S: 0.3%, Cu: 0.01%, Fe: 0.01%, Zn:0.01, Mn: 0.01%, B: 0.001%, Mo: 0.01, Chất hữu cơ:20%, độ ẩm &amp;lt; 30%&lt;/p&gt;', '&lt;p&gt;\r\n	TH&amp;Agrave;NH PHẦN:&lt;/p&gt;\r\n&lt;p&gt;\r\n	N: 2.5% ; P&lt;sub&gt;2&lt;/sub&gt;O&lt;sub&gt;5&lt;/sub&gt;: 2.5% ; K&lt;sub&gt;2&lt;/sub&gt;O : 2.5%.Trung vi lượng&amp;nbsp; :Cao, 0.5%, Mg: 0.2%, S: 0.3%, Cu: 0.01%, Fe: 0.01%, Zn:0.01, Mn: 0.01%, B: 0.001%, Mo: 0.01, Chất hữu cơ:20%, độ ẩm&amp;nbsp; &amp;lt; 30%&lt;/p&gt;\r\n&lt;p&gt;\r\n	C&amp;Ocirc;NG DỤNG:&lt;/p&gt;\r\n&lt;p&gt;\r\n	Chứa chitin cải tạo đất, chống c&amp;aacute;c bệnh tuyến tr&amp;ugrave;ng rễ, thối rễ,gi&amp;uacute;p ph&amp;aacute;t triển rễ, tăng vi sinh vật c&amp;oacute; &amp;iacute;ch trong đất l&amp;agrave;m c&amp;acirc;y tươi tốt,cứng c&amp;aacute;p, giảm lượng ph&amp;acirc;n b&amp;oacute;n NPK.&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Chứa l&amp;acirc;n v&amp;agrave; trung vi lượng gi&amp;uacute;p c&amp;acirc;y ăn tr&amp;aacute;i, quả ngọt, l&amp;aacute;ng tr&amp;aacute;i, hấp thu dinh dưỡng cao, &amp;iacute;t bệnh tật, &amp;iacute;t phải d&amp;ugrave;ng thuốc trừ s&amp;acirc;u.&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Th&amp;iacute;ch hợp cho việc sản xuất tr&amp;aacute;i c&amp;acirc;y an to&amp;agrave;n.&lt;/p&gt;', 'phan-huu-co-sinh-hoc-chuyen-cay-an-trai', '', '', '', 0, 0, 16, 'upload/dau bo No1-thanh long copy.jpg', 0, '', '[phanhuuco][sanphammoi][][]', 0, 0, 'active', '2013-05-31 09:35:31', '', '2013-05-31 09:36:46', ''),
(19, 'default13699642171', '', '', '', '', '', '[phan-hc-cay-an-trai]', 'admin', 'Hữu cơ sinh học (chuyên cây có múi)', '&lt;p&gt;\r\n	TH&amp;Agrave;NH PHẦN: N: 2.5%, P2O5:2.5%, K2O: 2.5%, Hữu cơ: 20% Tổng vi lượng:CaO: 0.5%, Mg: 0.2%, Fe: 0.01%, S: 0.3%, Zn: 0.01%, Cu:0.01%, B: 0.001, Mn: 0.01%, Mo: 0.01% Độ ẩm&lt;/p&gt;', '&lt;p&gt;\r\n	TH&amp;Agrave;NH PHẦN:&lt;/p&gt;\r\n&lt;p&gt;\r\n	&lt;font size=&quot;3&quot;&gt;N: 2.5%, P&lt;sub&gt;2&lt;/sub&gt;O&lt;sub&gt;5&lt;/sub&gt;:2.5%, &lt;/font&gt;K&lt;sub&gt;2&lt;/sub&gt;&lt;font size=&quot;3&quot;&gt;O: 2.5%, Hữu cơ: 20%, Tổng vi lượng:CaO: 0.5%, Mg: 0.2%, Fe: 0.01%, S: 0.3%, Zn: 0.01%, Cu:0.01%, B: 0.001, Mn: 0.01%, Mo: 0.01%&lt;/font&gt;, Độ ẩm &amp;lt; 30%&lt;/p&gt;\r\n&lt;p&gt;\r\n	C&amp;Ocirc;NG DỤNG:&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Cải tạo đất, hạn chế c&amp;aacute;c bệnh tuyến tr&amp;ugrave;ng rễ,thối rễ,gi&amp;uacute;p ph&amp;aacute;t triển rễ, tăng vi sinh vật c&amp;oacute; &amp;iacute;ch trong đất l&amp;agrave;m c&amp;acirc;y tươi tốt,cứng c&amp;aacute;p, giảm lượng ph&amp;acirc;n b&amp;oacute;n NPK.&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Chứa l&amp;acirc;n v&amp;agrave; trung vi lượng gi&amp;uacute;p c&amp;acirc;y c&amp;oacute; m&amp;uacute;i lớn tr&amp;aacute;i, quả ngọt, l&amp;aacute;ng tr&amp;aacute;i, cơm dầy, hấp thu dinh dưỡng cao, &amp;iacute;t bệnh tật,&amp;iacute;t phải d&amp;ugrave;ng thuốc trừ s&amp;acirc;u bệnh.&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Hạn chế hiện tượng kh&amp;ocirc; m&amp;uacute;i, &amp;iacute;t nước.&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Hạn chế hiện tượng v&amp;agrave;ng l&amp;aacute; do thiếu Zn, Mg, Ca, ra rễ mới sau khi thu hoạch.&lt;/p&gt;\r\n&lt;p&gt;\r\n	- Th&amp;iacute;ch hợp cho việc sản xuất tr&amp;aacute;i c&amp;acirc;y an to&amp;agrave;n.&lt;/p&gt;', 'huu-co-sinh-hoc-chuyen-cay-co-mui', '', '', '', 0, 0, 17, 'upload/Cacl2 copy.jpg', 0, '', '[sanphammoi][][]', 0, 0, 'active', '2013-05-31 09:36:57', '', '2013-05-31 09:38:48', '');
